// DOM Elements
const todoInput = document.getElementById('todoInput');
const addTodoBtn = document.getElementById('addTodo');
const todoList = document.getElementById('todoList');
const timerDisplay = document.getElementById('timerDisplay');
const startTimerBtn = document.getElementById('startTimer');
const pauseTimerBtn = document.getElementById('pauseTimer');
const resetTimerBtn = document.getElementById('resetTimer');

// Todo List Functionality
let todos = JSON.parse(localStorage.getItem('todos')) || [];

// Add sound effects
const addSound = new Audio('data:audio/wav;base64,');
const completeSound = new Audio('data:audio/wav;base64,');

function saveTodos() {
    localStorage.setItem('todos', JSON.stringify(todos));
    addSound.play();
}

function createTodoElement(todo) {
    const li = document.createElement('li');
    li.className = `todo-item ${todo.completed ? 'completed' : ''}`;
    li.innerHTML = `
        <span class="todo-text">${todo.text}</span>
        <div class="todo-actions">
            <button class="pixel-button" onclick="toggleTodo(${todo.id})">${todo.completed ? 'Undo' : 'Done'}</button>
            <button class="pixel-button" onclick="deleteTodo(${todo.id})">Delete</button>
        </div>
    `;
    
    // Add hover effect
    li.addEventListener('mouseenter', () => {
        li.style.transform = 'translateX(5px)';
    });
    
    li.addEventListener('mouseleave', () => {
        li.style.transform = 'translateX(0)';
    });
    
    return li;
}

function renderTodos() {
    todoList.innerHTML = '';
    todos.forEach(todo => {
        todoList.appendChild(createTodoElement(todo));
    });
}

function addTodo(text) {
    if (text.trim()) {
        const todo = {
            id: Date.now(),
            text: text,
            completed: false
        };
        todos.push(todo);
        saveTodos();
        renderTodos();
        todoInput.value = '';
    }
}

function deleteTodo(id) {
    todos = todos.filter(todo => todo.id !== id);
    saveTodos();
    renderTodos();
}

function toggleTodo(id) {
    todos = todos.map(todo =>
        todo.id === id ? { ...todo, completed: !todo.completed } : todo
    );
    completeSound.play();
    saveTodos();
    renderTodos();
}

// Timer Functionality
let timerInterval;
let seconds = 0;
let isRunning = false;
let lastTimerState = { seconds: 0, isRunning: false };

function formatTime(totalSeconds) {
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;
    
    return `${String(hours).padStart(2, '0')}:${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;
}

function updateTimerDisplay() {
    timerDisplay.textContent = formatTime(seconds);
}

function startTimer() {
    if (!isRunning) {
        isRunning = true;
        timerInterval = setInterval(() => {
            seconds++;
            updateTimerDisplay();
            saveTimerState();
        }, 1000);
        startTimerBtn.disabled = true;
        pauseTimerBtn.disabled = false;
        addSound.play();
    }
}

function saveTimerState() {
    lastTimerState = {
        seconds,
        isRunning
    };
    localStorage.setItem('timerState', JSON.stringify(lastTimerState));
}

function loadTimerState() {
    const savedState = localStorage.getItem('timerState');
    if (savedState) {
        const state = JSON.parse(savedState);
        seconds = state.seconds;
        updateTimerDisplay();
    }
}

function pauseTimer() {
    if (isRunning) {
        isRunning = false;
        clearInterval(timerInterval);
        startTimerBtn.disabled = false;
        pauseTimerBtn.disabled = true;
    }
}

function resetTimer() {
    isRunning = false;
    clearInterval(timerInterval);
    seconds = 0;
    updateTimerDisplay();
    startTimerBtn.disabled = false;
    pauseTimerBtn.disabled = true;
}

// Event Listeners
addTodoBtn.addEventListener('click', () => addTodo(todoInput.value));
todoInput.addEventListener('keypress', (e) => {
    if (e.key === 'Enter') {
        addTodo(todoInput.value);
    }
});

startTimerBtn.addEventListener('click', startTimer);
pauseTimerBtn.addEventListener('click', pauseTimer);
resetTimerBtn.addEventListener('click', resetTimer);

// Initial render
renderTodos();
updateTimerDisplay();
loadTimerState();
pauseTimerBtn.disabled = true;

// Add keyboard shortcuts
document.addEventListener('keydown', (e) => {
    if (e.key === 'Enter' && document.activeElement === todoInput) {
        addTodo(todoInput.value);
    }
    if (e.key === ' ' && document.activeElement.tagName !== 'INPUT') {
        if (isRunning) {
            pauseTimer();
        } else {
            startTimer();
        }
        e.preventDefault();
    }
    if (e.key === 'r' && e.ctrlKey) {
        resetTimer();
        e.preventDefault();
    }
});

// Add cute cursor effects (optional)
document.addEventListener('mousemove', (e) => {
    const cursor = document.createElement('div');
    cursor.className = 'cursor-effect';
    cursor.style.left = e.pageX + 'px';
    cursor.style.top = e.pageY + 'px';
    document.body.appendChild(cursor);
    
    setTimeout(() => {
        cursor.remove();
    }, 500);
});

// Add this CSS to styles.css for cursor effect
const style = document.createElement('style');
style.textContent = `
    .cursor-effect {
        position: fixed;
        width: 8px;
        height: 8px;
        background: var(--primary-pink);
        border-radius: 50%;
        pointer-events: none;
        animation: cursorFade 0.5s linear forwards;
    }

    @keyframes cursorFade {
        0% {
            transform: scale(1);
            opacity: 0.5;
        }
        100% {
            transform: scale(0);
            opacity: 0;
        }
    }
`;
document.head.appendChild(style);
